beforeEach(function() {
    cy.visit('https://test-smp.herokuapp.com/');
    cy.get('#username').type('admin');
    cy.get('#password').type('12345');
    cy.get('.fourth').click();
    cy.visit('https://test-smp.herokuapp.com/descentralizacion/inicio_descentralizacion');
    cy.get(':nth-child(1) > :nth-child(3) > .btn').click();
});

describe('Gestión de Distritos', function() {

    it('Registrar distrito', () => {
        cy.get('[style="margin-top: 3%; margin-bottom: 3%;"] > .btn').click();
        cy.get('#cedula').type('11D001');
        cy.get('#nombre').type('Loja');
        cy.get('.col-lg-4 > .btn').click();
    });

    it('Listar y editar distritos', () => {
        cy.get('.col-lg-8 > :nth-child(2) > .btn').contains('Listar Distritos').click();
        cy.get('#example_filter > label > .form-control').clear().type('11D001');
        cy.get(':nth-child(1) > [style="text-align: center"] > .btn-success').click();

        cy.get('#nombre').clear().type('LOJA TEST');
        cy.get('.col-lg-4 > .btn').click();

    });

    it('Eliminar distrito', () => {
        cy.get('.col-lg-8 > :nth-child(2) > .btn').contains('Listar Distritos').click();
        cy.get('#example_filter > label > .form-control').clear().type('11D001');
        cy.get(':nth-child(1) > [style="text-align: center"] > .btn-danger').click();
    });

    it('Crear, listar y editar vinculación Distrito - Cantón', () => {
        cy.visit('https://test-smp.herokuapp.com/descentralizacion/listar_distrito');
        cy.get('#example_filter > label > .form-control').type('11D01C10');
        //Crear
        cy.get('.btn-primary').click();
        cy.get('#comboboxProvinciaVin').select('Loja');
        cy.get('#comboboxCantonVin').select('Catamayo');
        cy.get('.row > .btn').contains('Guardar').click();
    });
});

after(function() {
    cy.log('Terminando de probar distritos');
});