beforeEach(function() {
    cy.visit('https://test-smp.herokuapp.com/');
    cy.get('#username').type('admin');
    cy.get('#password').type('12345');
    cy.get('.fourth').click();
    cy.visit('https://test-smp.herokuapp.com/descentralizacion/inicio_descentralizacion');
});

describe('Gestión de Personal - Descentralización', function() {

    it('Vincular personal - descentralización', () => {
        cy.get(':nth-child(2) > [style="margin-left: 8%; margin-top: 2%; margin-bottom: 10%; margin-bottom: 10%;"] > .btn').click();
        cy.get('#example_filter > label > .form-control').type('1104879658');
        cy.get(':nth-child(1) > .sorting_1').click();

        cy.get('#comboboxDistrito').select('CATAMAYO');
        cy.get('#comboboxCircuito').select('CATAMAYO NORTE');
        cy.get('#comboboxSubcircuito').select('CATAMAYO NORTE 1');
        cy.get('[style="margin-left: 8%;"] > .btn').click();
    });

    it('Listar y editar personal - descentralización', () => {
        cy.get(':nth-child(3) > [style="margin-left: 8%; margin-top: 2%; margin-bottom: 10%; margin-bottom: 10%;"] > .btn').click();
        cy.get('#example_filter > label > .form-control').type('CATAMAYO NORTE 1');
        cy.get(':nth-child(1) > [style="text-align: center"] > .btn-success').click();

        cy.get('#comboboxDistrito').select('CATAMAYO');
        cy.get('#comboboxCircuito').select('CATAMAYO NORTE');
        cy.get('#comboboxSubcircuito').select('CATAMAYO NORTE 2');
        cy.get('[style="margin-left: 8%;"] > .btn').click();

    });

    it('Eliminar personal - descentralización', () => {
        cy.get(':nth-child(3) > [style="margin-left: 8%; margin-top: 2%; margin-bottom: 10%; margin-bottom: 10%;"] > .btn').click();
        cy.get('#example_filter > label > .form-control').type('CATAMAYO NORTE 2');

        cy.get(':nth-child(1) > [style="text-align: center"] > .btn-danger').click();
    });

});

after(function() {
    cy.log('Terminando de probar descentralización - personal');
});