beforeEach(function() {
    cy.visit('https://test-smp.herokuapp.com/');
    cy.get('#username').type('admin');
    cy.get('#password').type('12345');
    cy.get('.fourth').click();
    cy.visit('https://test-smp.herokuapp.com/descentralizacion/inicio_descentralizacion');
    cy.get('[style="margin-left: 8%; margin-top: 2%; margin-bottom: 3%;"] > .btn').click();
});

describe('GestiÃ³n de Subcircuitos', function() {

    it('Registrar subcircuito', () => {
        cy.get('[style="margin-top: 3%; margin-bottom: 3%;"] > .btn').click();
        cy.get('#codigo').type('SUBC001');
        cy.get('#nombre').type('Catamayo 1');
        cy.get('[type="radio"]').check('1');
        cy.get('#circuito').select('CATAMAYO NORTE');
        cy.get('.col-lg-4 > .btn').click();
    });

    it('Listar y editar subcircuitos', () => {
        cy.get('[style="margin-top: 2%; margin-bottom: 3%;"] > .btn').contains('Listar Subcircuitos').click();
        cy.get('#example_filter > label > .form-control').type('SUBC001');
        cy.get(':nth-child(1) > [style="text-align: center"] > .btn-success').click();

        cy.get('#nombre').clear().type('Catamayo 1 Test Update');
        cy.get('.col-lg-4 > .btn').click();
    });

    it('Eliminar subcircuito', () => {
        cy.get('[style="margin-top: 2%; margin-bottom: 3%;"] > .btn').contains('Listar Subcircuitos').click();
        cy.get('#example_filter > label > .form-control').type('SUBC001');
        cy.get(':nth-child(1) > [style="text-align: center"] > .btn-danger').click();
    });

});

after(function() {
    cy.log('Terminando de probar subcircuitos');
});