//Mocha (realizar test) y Chai (librería de aserciones)
describe('Sesión de sistema', function() {
    it('Probar que la sesión sea correcta', () => {
        cy.visit('https://test-smp.herokuapp.com/');
        cy.get('#username').type('admin');
        cy.get('#password').type('12345');
        cy.get('.fourth').click();
        cy.get('h5').contains('admin');
        cy.get('.in-line').click();
        cy.get('.alert > :nth-child(2)').contains("La Sesión se Cerro Correctamente!");
    });
})