beforeEach(function() {
    cy.visit('https://test-smp.herokuapp.com/');
    cy.get('#username').type('admin');
    cy.get('#password').type('12345');
    cy.get('.fourth').click();
})

describe('Navegación', function() {

    it('Botón Mantenimientos', () => {
        cy.get(':nth-child(2) > #navbarDropdownPersonal').click();
        cy.get('[href="/mantenimiento/busqueda_vehiculo"]').contains('Registrar Mantenimiento').click();
        cy.get(':nth-child(2) > #navbarDropdownPersonal').click();
        cy.get('.dropdown-menu > [href="#"]').contains('Listar Mantenimientos').click();
    });

    it('Botón Personal', () => {
        cy.get(':nth-child(3) > #navbarDropdownPersonal').click();
        cy.get('[href="/personal/crear_personal"]').contains('Registrar Personal').click();
        cy.get(':nth-child(3) > #navbarDropdownPersonal').click();
        cy.get('[href="/personal/listar_personal"]').contains('Listar Personal').click();
    });

    it('Botón Vehículos', () => {
        cy.get('#navbarDropdownVehiculos').click();
        cy.get('[href="/vehiculos/tipo_vehiculo"]').contains('Registrar Vehículo').click();
        cy.get('#navbarDropdownVehiculos').click();
        cy.get('[href="/vehiculos/listar_vehiculo"]', { timeout: 70000 }).contains('Listar Vehículos').click();
    });

    it('Botón Descentralización', () => {
        cy.get('#navbarDropdownDescentralizacion').click();
        cy.get('[href="/descentralizacion/inicio_distrito"]').contains('Distritos').click();
        cy.get('#navbarDropdownDescentralizacion').click();
        cy.get('[href="/descentralizacion/inicio_circuito"]').contains('Circuitos').click();
        cy.get('#navbarDropdownDescentralizacion').click();
        cy.get('[href="/descentralizacion/inicio_subcircuito"]').contains('Subcircuitos').click();
        cy.get('#navbarDropdownDescentralizacion').click();
        cy.get('[href="/descentralizacion/vpd"]').contains('Personal - Descentralización').click();
        cy.get('#navbarDropdownDescentralizacion').click();
        cy.get('[href="/descentralizacion/vvd"]').contains('Vehículos - Descentralización').click();
        cy.get('#navbarDropdownDescentralizacion').click();
        cy.get('[href="/descentralizacion/listar_vpd"]').contains('Listar Personal - Descentralización').click();
        cy.get('#navbarDropdownDescentralizacion').click();
        cy.get('[href="/descentralizacion/listar_vvd"]').contains('Listar Vehículos - Descentralización').click();
    });

    it('Botón Usuarios', () => {
        cy.get('#navbarDropdownUsuarios').click();
        cy.get('[href="/usuarios/crear_usuario"]').contains('Registrar Usuarios').click();
        cy.get('#navbarDropdownUsuarios').click();
        cy.get('[href="/usuarios/listar_usuarios"]').contains('Listar Usuarios').click();
    });
})

after(function() {
    cy.log('Terminando de probar navegación dropdown');
})