beforeEach(function() {
    cy.visit('https://test-smp.herokuapp.com/');
    cy.get('#username').type('admin');
    cy.get('#password').type('12345');
    cy.get('.fourth').click();
    cy.visit('https://test-smp.herokuapp.com/personal/inicio_personal');
});
describe('GestiÃ³n de personal', function() {

    it('Registrar personal', () => {
        cy.get('[style="margin-top: 3%; margin-bottom: 3%;"] > .btn').contains('Registrar Personal').click();
        cy.get('#cedula').type('1010101010');
        cy.get('#nombrespersonal').type('Luis Manuel');
        cy.get('#apellidospersonal').type('Requelme Guarnizo');
        cy.get('select').select('CABO SEGUNDO');
        cy.get('.col-lg-4 > .btn').click();
    });

    it('Listar y editar personal', () => {
        cy.get('[style="margin-top: 2%; margin-bottom: 3%;"] > .btn').click();
        cy.get('#example_filter > label > .form-control').clear().type('1010101010');
        cy.get(':nth-child(1) > [style="text-align: center"] > .btn-success').click();

        cy.get('#nombrespersonal').clear().type('nombre personal test');
        cy.get('.col-lg-4 > .btn').click();
    });

    it('Eliminar personal', () => {
        cy.get('[style="margin-top: 2%; margin-bottom: 3%;"] > .btn').click();
        cy.get('#example_filter > label > .form-control').clear().type('1010101010');
        cy.get(':nth-child(1) > [style="text-align: center"] > .btn-danger').click();
    });
})

after(function() {
    cy.log('Terminando de probar personal');
})