beforeEach(function() {
    cy.visit('https://test-smp.herokuapp.com/');
    cy.get('#username').type('admin');
    cy.get('#password').type('12345');
    cy.get('.fourth').click();
    cy.visit('https://test-smp.herokuapp.com/usuarios/inicio_usuarios');
});
describe('GestiÃ³n de usuarios', function() {

    it('Registrar usuario', () => {
        cy.get('[style="margin-top: 3%; margin-bottom: 3%;"] > .btn').click();

        cy.get('#cedula').type('1001001000');
        cy.get('#nombres').type('Ximena Valentina');
        cy.get('#apellidos').type('Tacuri Rosales');
        cy.get('#rango').select('SUBTENIENTE');
        cy.get('#username').type('xval33');
        cy.get('#password').type('val12345');
        cy.get('[type="radio"]').check('1');
        cy.get('.col-lg-4 > .btn').click();
    });

    it('Listar y editar usuarios', () => {
        cy.get('[style="margin-top: 2%; margin-bottom: 3%;"] > .btn').click();
        cy.get('#example_filter > label > .form-control').type('1001001000');

        cy.get('[style="text-align: center"] > .btn-success').click();
        cy.get('#rango').select('POLICIA');
        cy.get('#password').type('val12345');
        cy.get('.col-lg-4 > .btn').click();
    });

    it('Asignar rol a usuario', () => {
        cy.get('[style="margin-top: 2%; margin-bottom: 3%;"] > .btn').click();
        cy.get('#example_filter > label > .form-control').clear().type('1001001000');

        cy.get('.btn-primary').click();
        cy.get('#comboboxRoles').select('USUARIO');
        cy.get('#submitP').click();
    });

    //cuando no se asigna rol
    it('Eliminar usuario', () => {
        cy.get('[style="margin-top: 2%; margin-bottom: 3%;"] > .btn').click();
        cy.get('#example_filter > label > .form-control').clear().type('1001001000');

        cy.get('[style="text-align: center"] > .btn-danger').click();
    });
});

after(function() {
    cy.log('Terminando de probar usuarios');
});