beforeEach(function() {
    cy.visit('https://test-smp.herokuapp.com/');
    cy.get('#username').type('admin');
    cy.get('#password').type('12345');
    cy.get('.fourth').click();
    cy.visit('https://test-smp.herokuapp.com/vehiculos/inicio_vehiculo');
});
describe('Gestión de vehículos', function() {

    it('Registrar vehículo', () => {
        cy.get('[style="margin-top: 3%; margin-bottom: 3%;"] > .btn').click();
        cy.get('[style="position: relative; padding-top: 2%; margin-bottom: 2%;"] > .row > .btn').click();

        cy.get('#tipo').select('AUTO');
        cy.get('#modelo').select('RIO SEDAN');
        cy.get('#chasis').type('772H2XA59BWDY2547');
        cy.get('#anioFabrica').type('2018');
        cy.get('#colorDos').select('GRIS');
        cy.get('#contrato').select('2');

        cy.get('#marca').select('KIA');
        cy.get('#motor').type('52WVC10337');
        cy.get('#placa').type('LBA1111');
        cy.get('#colorUno').select('GRIS');
        cy.get('#ramv_cpn').type('T00874111');
        cy.get('#km').clear().type('15000');

        cy.get('.col-lg-4 > .btn').click();

    });

    it('Listar y editar vehículos', () => {
        cy.get('[style="margin-top: 2%; margin-bottom: 3%;"] > .btn').click();
        cy.get('#example_filter > label > .form-control').clear().type('LBA1111');

        cy.get(':nth-child(1) > [style="text-align: center"] > .btn-success').click();
        cy.get('#km').clear().type('20000');
        cy.get('.col-lg-4 > .btn').click();
    });

    it('Eliminar vehículo', () => {
        cy.get('[style="margin-top: 2%; margin-bottom: 3%;"] > .btn').click();
        cy.get('#example_filter > label > .form-control').clear().type('LBA1111');
        cy.get(':nth-child(1) > [style="text-align: center"] > .btn-danger').click();
    });
})

after(function() {
    cy.log('Terminando de probar vehículos');
})